/**
 * Haal uit de excel sheet het aantal regel (of eigenlijk 'einde row')
 */
String getMaxRows(File excelFile, String artSheetName, String dataSheetName) {
	Workbook workbook   = Workbook.getWorkbook(excelFile)

	dataSheetName.tokenize(",").each {
		def sh = it.trim();
		if (workbook.getSheet(sh) == null) {
			throw new Exception ("Kan de sheet '${sh}' niet vinden");
		}	
	}
	Sheet sheet = workbook.getSheet(artSheetName);
	if (sheet == null) {
		throw new Exception ("Kan de sheet '${artSheetName}' niet vinden");		
	}
	return sheet.getRows().toString()
}

