WsdlPropertiesTestStep counters = currentTestcase.getTestStepByName("Counters") ;
Workbook workbook   = Workbook.getWorkbook(new File(excelPath, excelFileName));
Sheet controlSheet = workbook.getSheet(context.expand('${#TestCase#art_input_sheet}'));

List<String> kolomNamen = Arrays.asList ("Testgeval", "VolgNr", "Beschrijving", "Status", "PrepareData", "Pre Query", "SendRequest", "Soap Response Query", "DB Query", "Post Query");
