void loadControlregelFromExcel(Sheet sheet, WsdlPropertiesTestStep counters, Integer[] kolomNrs, List<String> kolomNamen, WsdlPropertiesTestStep properties){
	Integer huidigRij = Integer.valueOf(counters.getPropertyValue("executeRow")) - 1;
	if (huidigRij != null && huidigRij > 0) {
		// zoek de eerste niet lege rij OP of NA de huidgRij en (kleiner dan de MaxRij)
		Cell cellTestgeval = sheet.getCell(kolomNrs[0], huidigRij);
		while (cellTestgeval == null || cellTestgeval.getContents() == null && huidigRij < 1000) {
			huidigRij = huidigRij + 1;
			cellTestgeval = sheet.getCell(kolomNrs[0], huidigRij);
		}
		if (cellTestgeval == null || cellTestgeval.getContents() == null) {
			log.error ("Kan geen controle cel meer vinden.")
			counters.setPropertyValue(executeRow , "0");
			return;
		}
		for (int i=0; i< kolomNrs.length; i++) {
			if (kolomNrs[i] != null) {
				// dit is een echt bestaand kolom
				Cell cell = sheet.getCell(kolomNrs[i], huidigRij);
				if (cell != null)  {
//					log.info("setting " + kolomNamen.get(i) + " to " +  cell.getContents());
					properties.setPropertyValue(kolomNamen.get(i) , cell.getContents());
				}
			}
		}
		counters.setPropertyValue("executeRow" , (huidigRij + 1).toString());
		return;
	}
}

/**
 * Verwijder alle property van de datasource
 */
void resetDataSource(WsdlPropertiesTestStep datasource) {
	List properties = datasource.getPropertyList()
	for(item in properties){
    		datasource.removeProperty(item.getName());
	}
}



Integer[] initKolomNrs(Sheet sheet, List<String> kolomNamen) {
	Integer[] kolomNrs = new Integer[kolomNamen.size()];
	for ( int i=0; i < kolomNamen.size(); i++) {
		Cell cell = getTestgevalKolomNummer(sheet, kolomNamen.get(i));
		if (cell != null) {
			kolomNrs[i] = cell.getColumn();
		}
	}
	return kolomNrs;
}

Cell getTestgevalKolomNummer (Sheet sheet, String headerNaam) {
	// zoek in de header (kolom 0, row [0..200]
	Cell cell  = sheet.findCell(headerNaam, 0, 0, 200, 0, false);
	if (cell == null) {
		//TODO maak goto om overige steps te skippen.
		throw new Exception("Kan geen Cell vinden: ${headerNaam}");
	} else {
//		int kolomNr = cell.getColumn();
//		log.info ("Gevolden kolomnr: " + kolomNr);
		return cell;
	}
}

