/**
 * Verwijder alle property van de datasource
 */
void resetDataSource(WsdlPropertiesTestStep datasource) {
	List properties = datasource.getPropertyList()
	for(item in properties){
    		datasource.removeProperty(item.getName());
	}
}

void loadDataFromExcel(Sheet sheet, Cell[] attribuutNaamCellen, Integer kolomTestGeval, WsdlPropertiesTestStep properties) {
	for (int i=0; i< attribuutNaamCellen.length; i++) {
		Cell cell = sheet.getCell(kolomTestGeval, attribuutNaamCellen[i].getRow());
		if (cell != null) {
			properties.setPropertyValue(attribuutNaamCellen[i].getContents(), cell.getContents());
		}
	}
}

def getKolomNummerVanTestGeval(def workbook, def sheets, String testGeval) {
	Sheet sheet;
	Cell cellTestGeval

	sheets.tokenize(",").any {
		def sh = it.trim();
		sheet = workbook.getSheet(sh);
		cellTestGeval = sheet.findCell(testGeval, 0, 0, 2000, 0, false);

		return cellTestGeval != null
	}

	if (cellTestGeval == null) {
		log.error("[Read Excel and Write DataSource Values] Kan geen testgeval vinden met de waarde: ${testGeval}");
		throw new Exception("Kan geen testgeval vinden met de waarde: ${testGeval}")
	} else {
		return [sheet, cellTestGeval.getColumn()];
	}
}

/**
 * Haal een lijst van cellen met de attribuutnamen.
 *
 */
Cell[] getAttribuutNaamCellen(Sheet sheet) {
	Cell[] attribuutNaamCellen = sheet.getColumn(0);
	ArrayList<Cell> goedeCellen = new ArrayList<Cell>();
	ArrayList<String> uniekeAttribuutNamen = new ArrayList<String>();

	for (int i=1; i< attribuutNaamCellen.length; i++) {
		String attribuutNaam = attribuutNaamCellen[i].getContents();
		if (!attribuutNaam.equals("") && !attribuutNaam.equals("Testgeval")) {
			if (!attribuutNaam.startsWith("|") && uniekeAttribuutNamen.contains(attribuutNaam)) {
				String errMsg = "[Read Excel and Write DataSource Values] Attribuutnaam: '" + attribuutNaam +
					"', cell[" + (1 + attribuutNaamCellen[i].getColumn()) + "," + (1 + attribuutNaamCellen[i].getRow()) + "] is niet uniek.";
				log.error(errMsg);
				throw new Exception(errMsg);
			}
			uniekeAttribuutNamen.add(attribuutNaam);
			goedeCellen.add(attribuutNaamCellen[i]);
		}
	}
	return goedeCellen.toArray(new Cell[goedeCellen.size()]);
}

