import groovy.sql.Sql
import nl.bzk.brp.soapui.utils.date.DateSyntaxTranslatorUtil

String USERNAME = context.expand('${#Project#ds_user}')
String PASSWORD = context.expand('${#Project#ds_password}')
String URL      = context.expand('${#Project#ds_url}')
String DRIVER   = context.expand('${#Project#jdbcDriver}')

String DB_RESET_PERSONEN = context.expand('${Control Values#PrepareData}');

def props = [user: USERNAME, password: PASSWORD, allowMultiQueries: 'true'] as Properties;
def sql = Sql.newInstance(URL, props, DRIVER);

// PrepareData
DB_RESET_PERSONEN.tokenize(",").each {
	def bsn = Integer.valueOf(it.trim());

	def sqlRows = sql.rows("select sql from test.referentie where bsn = ${bsn}")

	if (sqlRows.size() == 0) {
		// Als er geen restore statements gevonden wordt voor de bsn, dan voor alleen de delete uit.
		def sqlStatement = 
		"""DELETE FROM kern.relatie r WHERE r.id in  (SELECT r2.id from kern.relatie r2, kern.betr b, kern.pers p  WHERE r2.id = b.relatie and b.pers = p.id and p.bsn IN (${bsn}));
		   DELETE FROM kern.pers p WHERE p.bsn in (${bsn});"""
		sql.execute sqlStatement;
	} else {
		def sqlStatements = "";
		sqlRows.each {
			sqlStatements = sqlStatements + "\n" + it.sql
		}
		sql.execute sqlStatements;
	}
}

