/**
 * Bouwt de SOAP request op. Deze script leest een xml template in en vervangt alle place holders met
 * waarden uit de 'CSV DataSource Values'.
 */
import groovy.xml.XmlUtil
import groovy.util.slurpersupport.NodeChild
import nl.bzk.brp.soapui.utils.date.DateSyntaxTranslatorUtil

def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)

// Input file
String PROJECT_PATH      = context.expand( '${#TestCase#test_script_path}' );
String REQUEST_TEMPLATE  = "${PROJECT_PATH}/request_template.xml"
String CURRENT_TIMESTAMP = context.expand('${Control Values#ART-executionTimestamp}');

def supportedRegExp = [
	"\\[nil\\]",
	"\\[-[1-9]\\]|\\[\\]",
	"\\[vandaag.*\\]",
	"\\[moment.*\\]"
]

def regExp = supportedRegExp.join("|")

File xmlFile = new File(REQUEST_TEMPLATE)
def xml = context.expand(xmlFile.text);

NodeChild gpathResult  = new XmlSlurper().parseText(xml)


//Vervang alle code woorden/formules. 
replaceNodes (CURRENT_TIMESTAMP, gpathResult, regExp)
return XmlUtil.serialize(gpathResult);


//Function
def replaceNodes (String currentTimestamp, NodeChild node, String withText) {
	Map<String, String> maNil = new HashMap();
	maNil.put("ns:nil", "true"); // hou rekening mee met de namespace.
	if (node.size() > 0){
		node.'*'.each{
			replaceNodes (currentTimestamp, it, withText)
		}

		node.find{it.text().matches(withText)}.each {
			//In node.find geeft alle nodes terug waarin de childrens de text bevat.
			//<node1><child1>[]</child1</node1> met find wordt node1 en child1 teruggeven
			if(!(it.children().size() > 0)) {
				switch(it.text()) {
					case ~/\[nil\]/:
						// zet de attribute op "nil=true" en geen inhoud.
						node.replaceBody(null);
						node.attributes().putAll(maNil);
						break;
					case ~/\[-[1-9]\]|\[\]/:
						int parentFromNode = 0
						if (it.text() != "[]") {
							parentFromNode = it.text()[2].toInteger();
						}

						// Verwijder parent aangegen door een negatief getal, -1 is
						// directe parent -2 is parent daarboven
						def nodeToRemove = node;
						for (int i = 0; i < parentFromNode; i++) {
							nodeToRemove = nodeToRemove.parent();
						}
						nodeToRemove.replaceNode {}
						break;
					case ~/\[vandaag.*\]/:
						node.replaceBody(DateSyntaxTranslatorUtil.parseString(currentTimestamp, it.text()));
						break;
					case ~/\[moment.*\]/:
						node.replaceBody(DateSyntaxTranslatorUtil.parseString(currentTimestamp, it.text()));
						break;
					default:
						log.info ("[Build Request] default: " + it.text());
				}
			}
		}
	}
}

// Output file
String              PROJECT_PATH         = context.expand( '${#TestCase#test_script_path}' );
String              TESTGEVAL            = context.expand('${Control Values#Testgeval}');
