	def file = new File(filePath)
	file.write(content, "UTF-8")
}

 /**
  * Bereid de xPath assertions voor voor het request.
  */
 import groovy.xml.XmlUtil
 import com.eviware.soapui.support.XmlHolder
 import com.eviware.soapui.impl.wsdl.teststeps.WsdlTestRequestStep

 import com.eviware.soapui.config.TestAssertionConfig;
 import com.eviware.soapui.impl.wsdl.teststeps.assertions.basic.XPathContainsAssertion;
 import com.eviware.soapui.impl.wsdl.WsdlSubmitContext;

 def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
 def myTestCase = context.testCase

 // Input file
 String PROJECT_PATH         = context.expand( '${#TestCase#test_script_path}' );
 String TESTGEVAL            = context.expand('${Control Values#Testgeval}');
 String SEQNR                = context.expand( '${Control Values#VolgNr}' )
 String ACTUAL_PATH          = context.expand( '${#TestCase#actual_dir}' );
 String EXPECTED_RESULT_FILE = "${PROJECT_PATH}/ExpectedResults/response/${TESTGEVAL}-${SEQNR}-soapresponse.xml"
 String ACTUAL_RESPONSE_FILE = "${PROJECT_PATH}/${ACTUAL_PATH}/soap/response/${TESTGEVAL}-${SEQNR}-soapresponse.xml";

 // Gegevens voor assertions
 String PREFIX          = "brp";
 String NAMESPACE       = "http://www.bprbzk.nl/BRP/0001";
 String PREFIX_SOAP     = "soap";
 String NAMESPACE_SOAP  = "http://schemas.xmlsoap.org/soap/envelope/";
 String SOAP_RESULT_TAG = context.expand('${Control Values#Soap Response Query}');

 // Kies welke namespace er gebruikt moet worden
 String namespace;
 String prefix;
 if (SOAP_RESULT_TAG.contains("soap:")) {
    namespace = NAMESPACE_SOAP;
    prefix = PREFIX_SOAP;
 } else if (SOAP_RESULT_TAG.contains("brp:")) {
    namespace = NAMESPACE;
    prefix = PREFIX;
 }

 // Lees de expected results
 String xml = context.expand(new File(EXPECTED_RESULT_FILE).getText("UTF-8"));

 // Vraag gedeelte op van de expected results om de vergelijking erop los te laten
 def node = haalNodeOpUitXml(xml, SOAP_RESULT_TAG, prefix, namespace);


 // Maak de assertions aan
 if (node == null) {
    throw new Exception("De node kon niet gevonden worden in de expected result met de opgegeven soap_result_tag: " + SOAP_RESULT_TAG);
 } else {
    def response = context.expand( '${Test Request#Response#declare namespace soap=\'http://schemas.xmlsoap.org/soap/envelope/\'; //soap:Body[1]}' )
    maakRequestAssertions(SOAP_RESULT_TAG, prefix, namespace, node, response);
 }

 /**
  * Haal node op uit de expected xml
  */
 def haalNodeOpUitXml(String xml, String xpath, String prefix, String namespace) {
    def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context);
    def holder  = groovyUtils.getXmlHolder(xml);
    holder.namespaces[prefix] = namespace;
    return holder.getDomNode(xpath);
 }

 /**
  * Maak assertion aan.
  */
 void maakRequestAssertions(String soap_result_tag, String prefix, String namespace, def node, def actualResponse) {
    XPathContainsAssertion assertion;
    assertion = new XPathContainsAssertion( TestAssertionConfig.Factory.newInstance(), null );
    assertion.setPath("declare namespace ${prefix}='${namespace}';${soap_result_tag}");
    assertion.setAllowWildcards(true);
    assertion.setIgnoreComments(true);
    assertion.setExpectedContent(XmlUtil.serialize(node));
    assertion.assertContent(actualResponse, new WsdlSubmitContext( null ), "");
}

