/**
 * Leest de assertions uit en stop deze in de Assertion Results properties stap.
 */
import com.eviware.soapui.impl.wsdl.teststeps.WsdlPropertiesTestStep;

def testCase = context.testCase;

// DataSource Values
WsdlPropertiesTestStep ASSERTION_RESULTS = testCase.getTestStepByName("Assertion Results") ;

//Controlleer errors in alle stappen
checkStepErrors(ASSERTION_RESULTS);
logStatus();


/**
 * Controlleerd errors in alle stappen.
 *
 * Deze block kan niet als individueel stap uitgevoerd worden, het moet uitgevoerd worden door de testRunner, dus de TestSteps in zijn geheel.
 */
String checkStepErrors(WsdlPropertiesTestStep assertionResults) {
	String debugMessage = "";
	String message = "";
	String status = "";
	def resultaten = testRunner.getResults()
	resultaten.each {
		if (it.getStatus() == com.eviware.soapui.model.testsuite.TestStepResult.TestStepStatus.FAILED) {			
			String error = "[${it.getTestStep().name}] - Status: ${it.getStatus()} - Error: ${it.getError()} - Message: ";
			it.getMessages().each {
				error = error + it;
			}
			debugMessage = debugMessage + error + "\n";

			if (it.getError() instanceof com.eviware.soapui.model.testsuite.AssertionException) {
				message = message + "[${it.getTestStep().name}] - "
				it.getMessages().each {
					message =  message + it + "\n"
				}
			} 
		}
	}



	if (debugMessage != "") {
		status = com.eviware.soapui.model.testsuite.TestStepResult.TestStepStatus.FAILED.toString();
	} else {
		status = com.eviware.soapui.model.testsuite.TestStepResult.TestStepStatus.OK.toString();
		message = context.expand( '${Control Values#Status}' );
	}
	assertionResults.setPropertyValue("Status", status);
	assertionResults.setPropertyValue("Assertion", message);
	assertionResults.setPropertyValue("debug_log", debugMessage);


	resultaten.clear();
	return debugMessage;
}


/**
 * Hou een lijst van alle errors en status, in geval we niet meer naar DataSink willen wegschrijven.
 */
void logStatus() {
	WsdlPropertiesTestStep controlValues = context.testCase.getTestStepByName("Control Values") ;
	WsdlPropertiesTestStep ASSERTION_RESULTS = context.testCase.getTestStepByName("Assertion Results") ;

	String Testgeval = controlValues.getPropertyValue("Testgeval");
	List statusList = context.get("statusList");

	if (Testgeval != null && !Testgeval.equals("")) {
		Properties assertProperties = new Properties();
		String debug_log = ASSERTION_RESULTS.getPropertyValue("debug_log");
		String status = ASSERTION_RESULTS.getPropertyValue("Status");
		String assertion = ASSERTION_RESULTS.getPropertyValue("Assertion");

		assertProperties.put("Testgeval",controlValues.getPropertyValue("Testgeval"));
		assertProperties.put("VolgNr",controlValues.getPropertyValue("VolgNr"));
		assertProperties.put("Beschrijving",controlValues.getPropertyValue("Beschrijving"));
		if (null != debug_log) {
			assertProperties.put("debug_log",ASSERTION_RESULTS.getPropertyValue("debug_log"));
		}
		if (null != status) {
			assertProperties.put("Status",status);
		}
		if (null != assertion) {
			assertProperties.put("Assertion",assertion);
		}
		if (statusList != null) {
			statusList.add(assertProperties);
			context.put("statusList", statusList);
		}
	}
}

/**
 * Verwijder alle property van de datasource
 */
void resetDataSource(WsdlPropertiesTestStep datasource) {
	List properties = datasource.getPropertyList()
	for(item in properties){
    		datasource.removeProperty(item.getName());
	}
}

