/**
 * Deze stap haalt alle asserts uit de context en schrijft naar een 'resultaten.xsl' bestand. De xsl bestand is een 'kopie' 
 * van een leeg 'result_template.xsl'
 * Als de result_template.xsl niet bestaat of als er iets fout gaat, wordt alle informatie weggeschreven naar een text bestand
 * 		'report.txt'
 * Als een van de kolomnamen onbekend is, wordt ook een fout gegenererrd en alles wordt naar txt file weggeschreven.
 */
import jxl.*;
import jxl.write.*;
import jxl.format.UnderlineStyle;
import jxl.write.biff.RowsExceededException;

String             	PROJECT_PATH             = context.expand('${#TestCase#test_script_path}');
String 			EXCEL_PATH 			= PROJECT_PATH + "/" + context.expand('${#TestCase#actual_dir}') + "/" + "report"
String 			TEMPLATE_PATH 			= context.expand('${#Project#projectDir}')
String 			sheetName = "resultaat";

File xlsFile = new File (EXCEL_PATH, "resultaten.xls");
File voorbeeldXls = new File(TEMPLATE_PATH,"result_template.xls");

	if (voorbeeldXls.exists()) {
		try {
			writeExcelReport(voorbeeldXls, xlsFile, sheetName)
		} catch (Exception e) {
			writeFileReport(new File(EXCEL_PATH, "report.txt"), "[Create Excel Report] " + e.getMessage())
			throw e;
		}
	} else {
		writeFileReport(new File(EXCEL_PATH, "report.txt"), null)		
	}

/**
 * Schijf de rapportage weg naar een bestand, aangezien we geen excel kunnen aanmaken.
 *
 */
void writeFileReport(File txtFile, String extraText) {
	List statusList = context.get("statusList");
	txtFile.write("");
	if (statusList != null) {
		for (Properties prop : statusList) {
			txtFile.append(prop.toString())
			txtFile.append("\n")
		}
	}
	if (null != extraText) {
		txtFile.append(extraText)
		txtFile.append("\n")
	}


}

/**
 * Schrij de resultaten naar een nieuw excel bestand (afgeleid uit een template excel)
 */
void writeExcelReport(File voorbeeldXls, File xlsFile, String sheetName) throws Exception {
	WritableWorkbook workbook = null;
	WritableSheet reportSheet = null;
	List statusList = context.get("statusList");
	if (statusList != null) {
		try {
			// maak een copie van de template
			workbook = Workbook.createWorkbook(xlsFile, Workbook.getWorkbook(voorbeeldXls));
			reportSheet = workbook.getSheet(sheetName);
			if (reportSheet == null) {
				throw new Exception ("Kan geen sheetnaam '${sheetName}' vinden");
			}

			List<String> kolomNamen = Arrays.asList ("Testgeval", "VolgNr", "Beschrijving", "Status", "Assertion", "debug_log");
			Integer[] kolomNrs = initKolomNrs(reportSheet, kolomNamen);
			// begin vanaf de laaste regel (de rowNr begint met '0', dus is het goed)
			// Wat we zouden willen is dat de propertyNaam ook 'Status', 'Assertion', 'debug_log' willen hebben.
			int rowNr = reportSheet.getRows();
			for (Properties prop : statusList) {
				reportSheet.addCell(new Label(kolomNrs[0], rowNr, prop.getProperty("Testgeval")));
				reportSheet.addCell(new Label(kolomNrs[1], rowNr, prop.getProperty("VolgNr")));
				reportSheet.addCell(new Label(kolomNrs[2], rowNr, prop.getProperty("Beschrijving")));
				reportSheet.addCell(new Label(kolomNrs[3], rowNr, prop.getProperty("Status")));
				reportSheet.addCell(new Label(kolomNrs[4], rowNr, prop.getProperty("Assertion")));
				reportSheet.addCell(new Label(kolomNrs[5], rowNr, prop.getProperty("debug_log")));
				rowNr++;
			}
		} finally {
		    	workbook.write();
		    	workbook.close();
		}	

	}

}


/**
 * Haal alle kolom nummers aan de hand van kolomnamen.
 */
Integer[] initKolomNrs(Sheet sheet, List<String> kolomNamen) {
	Integer[] kolomNrs = new Integer[kolomNamen.size()];
	for ( int i=0; i < kolomNamen.size(); i++) {
		Cell cell = getHeaderKolomNummer(sheet, kolomNamen.get(i));
		if (cell != null) {
			kolomNrs[i] = cell.getColumn();
		}
	}
	return kolomNrs;
}

Cell getHeaderKolomNummer (Sheet sheet, String headerNaam) {
	// zoek in de header (kolom 0, row [0..200]
	Cell cell  = sheet.findCell(headerNaam, 0, 0, 200, 0, false);
	if (cell == null) {
		//TODO maak goto om overige steps te skippen.
		throw new Exception("Kan geen Cell vinden: '${headerNaam}'");
	} else {
		return cell;
	}
}

