/**
 * Lees properties van file en stop deze in de testCase properties.
 */
void loadPropertiesFromFile(String dir, String filename) {
	def properties = new java.util.Properties();
	properties.load( new java.io.FileInputStream( new File(dir, filename)));
	log.info("[TestCase Setup Script] Goed geladen bestand: ${dir}, ${filename}");
	def names = [];
	names = properties.propertyNames();
	while( names.hasMoreElements() ) {
		def name = names.nextElement();
		testRunner.testCase.setPropertyValue(name, properties.getProperty(name))
	}
}

/**
 * Stel de interface en operation in van de soap request.
 */
void setTestRequestOperation() {
	def testRequestStep  = testRunner.getTestCase().getTestStepByName("Test Request")

	if (testRequestStep == "") {
		def alert = com.eviware.soapui.support.UISupport;
		alert.showErrorMessage("Kan de SOAP request stap \"Test Request\" niet vinden, dit is een exceptionele fout. Heropen de ARTengine project zonder wijzigingen op te slaan.")
	}	



	String interfaceName = context.expand('${#TestCase#soap_interface}');
	String operationName = context.expand('${#TestCase#soap_operation}');

	def soap_endpoint    = context.expand( '${#TestCase#soap_endpoint}' )
	String endPoint	 = "\${#Project#${soap_endpoint}}";

	// Haal de operatie op
	def myInterface = (com.eviware.soapui.impl.wsdl.WsdlInterface) testRunner.testCase.testSuite.project.getInterfaceByName(interfaceName);
	def myOperation = myInterface.getOperationByName(operationName);
	// Update de huidige request met de nieuwe operatie, dit is nodig om ook de auth settings mee te krijgen in de nieuwe request
	testRequestStep.setOperation(myOperation);
	testRequestStep.properties['Endpoint'].value = endPoint;

	// Vervang Test Request. Simpelweg de nieuwe operatie zetten in de bestaande Test Request werkt niet, de Test Request moet in zijn geheel vervangen worden, anders kan de response
	// niet opgehaald worden.
	int currentLocationOfStep = testRunner.testCase.getIndexOfTestStep(testRequestStep);
	testRunner.testCase.importTestStep(testRequestStep, "New Test Request", currentLocationOfStep, true);
	// Een sleep omdat de UI raar kan doen
	sleep(100)
	testRunner.testCase.removeTestStep(testRequestStep);
	// Een sleep omdat de UI raar kan doen
	sleep(100)
	testRunner.getTestCase().getTestStepByName("New Test Request").setName("Test Request");
}

