	}
}

void createOutputDirectories(File baseOutputDir) {
	File fDir = null;
	List<String> directories = Arrays.asList(
		"/data/request",
		"/data/response",
		"/soap/request",
		"/soap/response",
		"/report",
		);
	for (int i=0; i< directories.size(); i++) {
		fDir = new File(baseOutputDir, directories.get(i));
		if (! fDir.exists()) {
			fDir.mkdirs();
		}
	}
}

