void loadCommandlineParameters(String projectDir) {
	// Laad nu de project settings, deze wordt alleen uitgevoerd als SOAPUI wordt gestart vanaf de command line.
	// bij de GUI, dient men deze met expliciet de hand te laden
	// Als geen paramaeter wordt meegeleverd, haal de ENV definitie uit de ENV/omgeving.properties; deze verwijst welk omgeving geladen moet worden.
	// In de env.proprtes staat de settings voor endpointURL(s), en dataasource indormatie.
	// -------------------------------------------
	def projectEnv = com.eviware.soapui.SoapUI.globalProperties.getPropertyValue("projectEnv")
	def testScript = com.eviware.soapui.SoapUI.globalProperties.getPropertyValue("testScript")
	if (projectEnv == null && testScript == null) {
		// laad de omgeving gedefinieerd in de ./ENV/omgeving.properties
		// in geval van GUI, lezen we de settings via de 'default.properties' bestand.
		// in geval van commandline, gaan we vanuit dat de default.properties niet bestaan en
		// 		'projectEnv' en de 'test_script_location' doorgegeven wordt als parameter.
		File defaultPropertiesFile = new File(projectDir, "default.properties");
		if (defaultPropertiesFile.exists()) {
			log.info("------ run from GUI, read settings from default.properties");
			loadPropertiesFromFile("${projectDir}", "default.properties");
			// default.properties bevat: [ENV, test_script_location [,dataSet] ]
		} else {
			loadPropertiesFromSettings(projectEnv, testScript);
		}
	} else {
		// blijkbaar runnen we vanaf commandline en hebben we parameters meegekregen.
		loadPropertiesFromSettings(projectEnv, testScript);		
	}
}

void loadPropertiesFromSettings(String projectEnv, String testScript) {
	log.info("------ run from commandline, settings must be correct: projectEnv= ${projectEnv}, testScript=${testScript}");
	if (projectEnv != null) {
		project.setPropertyValue("ENV", projectEnv);
	} else {
		log.warn "[Project Load Script] projectEnv is niet opgegeven via command line, gebruikt de waarde opgeslagen in ./default.properties - ENV: ${project.getPropertyValue("ENV")}"
	}
	if (testScript != null) {
		project.setPropertyValue("test_script_location", testScript);
	} else {
		log.warn "[Project Load Script] testScript is niet opgegeven via command line, gebruikt de waarde opgeslagen in ./default.properties - test_script_location: ${project.getPropertyValue("test_script_location")}"
	}	
}

void correctScriptPath(String projectDir) {
	// corrigeer nu als de test_script_location een 'relatieve pad is.
	File scriptPathFile = new File(projectDir, project.getPropertyValue("test_script_location"));
	if (scriptPathFile.exists()) {
		project.setPropertyValue("test_script_location", scriptPathFile.getAbsolutePath());
	} else {
		// blijkbaar was al een absolute path.
		scriptPathFile = new File(project.getPropertyValue("test_script_location"));
		if (scriptPathFile.exists()) {
			project.setPropertyValue("test_script_location", scriptPathFile.getAbsolutePath());
		} else {
			String errMsg = "Kan de scrippath ${scriptPathFile.absolutePath} niet vinden";
			log.error(errMsg);
			throw new IOException(errMsg);			
		}
	}
	log.info "--- ScriptPath=[${scriptPathFile.absolutePath}]"

}

void expandClassPath(String projectDir) {
	// Load external class path with groovy scripts
	def classpathHacker = new com.eviware.soapui.support.ClasspathHacker ()

	extraLibPathFile = new java.io.File(projectDir, "lib")
	extraLibPath = "file://" + extraLibPathFile.getPath()
	log.info "extraLibPath=" + extraLibPath

	classpathHacker.addFile(extraLibPathFile)
	com.eviware.soapui.support.ClasspathHacker.addFile(extraLibPathFile)
	com.eviware.soapui.support.ClasspathHacker.addURL( new URL(extraLibPath) )	
}


void loadPropertiesFromFile(String dir, String filename) {
	def properties = new java.util.Properties();
	properties.load( new java.io.FileInputStream(new File(dir, filename)));
	log.info("[Project Load Script] Goed geladen bestand: ${dir}/${filename}");
	def names = [];
	names =  properties.propertyNames();
	while( names.hasMoreElements() ) {
		def name = names.nextElement();
		 project.setPropertyValue(name, properties.getProperty(name))
	}
}

